const util = require("util");

module.exports = function log(msg) {
  if (typeof msg === "string") {
    console.log(msg);
  } else {
    //console.log(dump(msg, {pretty: true, depth: Infinity }));
    console.log(
      util.inspect(msg, { depth: null, showHidden: true, maxArrayLength: null })
    );
  }
};
